import React from 'react';
import TodoItem from './TodoItem';
import { useTransition, animated } from 'react-spring';

const TodoList = ({ todos, onDelete, onToggleComplete }) => {
  // Conditionally use the hook based on the length of todos
  const transitions = todos.length > 0 && useTransition(todos, todo => todo.id, {
    from: { opacity: 0, transform: 'translate3d(0, -20px, 0)' },
    enter: { opacity: 1, transform: 'translate3d(0, 0, 0)' },
    leave: { opacity: 0, transform: 'translate3d(0, -20px, 0)' },
  });

  return (
    <ul>
      {transitions && transitions.map(({ item, props, key }) => (
        <animated.div key={key} style={props}>
          <TodoItem
            key={item.id}
            todo={item}
            onDelete={() => onDelete(item.id)}
            onToggleComplete={() => onToggleComplete(item.id)}
          />
        </animated.div>
      ))}
    </ul>
  );
};

export default TodoList;
